import React from 'react'
import {
    View,
    StyleSheet,
    Text,
    TouchableOpacity,
    TextInput,
    Image
} from 'react-native';
import { UpperPart, RedBtn, BelowPart, Textinput  } from '../components';

 export const Settings = () =>{
     return(
         <View >
         <UpperPart/>
        <View style={styles.container}>
         <Image 
          style={styles.img} />
          <TouchableOpacity style={styles.btn}>
              <Text style={styles.btntxt}>EDIT PHOTO</Text>
          </TouchableOpacity>
          <Text style={styles.txt}>name</Text>
          <Textinput/>
          <Text style={styles.txt}>e-mail</Text>
          <Textinput/>
          <RedBtn/>
          </View>
          <BelowPart/>
          
                  </View>
     )
 } 
 const styles = StyleSheet.create({
container:{
    justifyContent:'center',
    alignItems:"center",
    paddingTop:15,
},

    img:{
borderWidth:4,
borderColor:"#E4BD30",
width:130,
height:130,
borderRadius:130,
},
btn:{
    backgroundColor:"#E4BD30",
    marginTop:15,
    marginBottom:20,
    width:208,
    height:35,
    alignItems:'center',
    justifyContent:'center',
    borderRadius:10,

},
btntxt:{
    fontSize:20,
    color:"white",
    fontWeight:"bold",

},

 })
