import React from 'react'
import {
    StyleSheet,
    View,
    Text,
    TextInput,
    Image,
    TouchableOpacity,
    Button,
  } from "react-native";
import Back from '../assets/back.png'
import ChefHat from '../assets/chef.png'
export const SignInPage = ({navigation}) =>{
    return(
        <View style={styles.container}>
        <View style={styles.loginview}>
            <TouchableOpacity 
            style={{right:100}}
           onPress={() => navigation.goBack()}    >
           <Image source={Back} style={styles.backbtn} /></TouchableOpacity>
         <Text style={styles.logintxt}>Sign In</Text>
           </View>
           <View style={styles.belowpart}>
 
     <View style={styles.inputview}>
     <TextInput style={styles.textinput} 
        placeholder='username'
         placeholderTextColor={'black'}/>
        <TextInput style={styles.textinput} 
        placeholder='e-mail'
        placeholderTextColor={'black'} />
        <TextInput style={styles.textinput} 
        placeholder='password'
         placeholderTextColor={'black'}/>
        <TextInput style={styles.textinput} 
        placeholder='confirm password'
        placeholderTextColor={'black'} />
        </View>
         
           <TouchableOpacity style={styles.btn}>
               <Text style={styles.btntxt}>CREATE</Text>
           </TouchableOpacity> 
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
container:{
    flex:1,
    backgroundColor:'#4689D3',
    paddingHorizontal:24,
    paddingVertical:50
},
loginview:{
    flexDirection:'row',
alignItems:'center',
paddingBottom:21,
justifyContent:'center',
},
logintxt:{
    color:"white",
   fontSize:45,
   fontWeight:'bold' 
},
backbtn:{
    width:27,
    height:27,
    // right:90
  
},
belowpart:{
    marginTop:20,
   paddingVertical:20,
    backgroundColor:'white',
    borderRadius:40,
    alignItems:'center',
    // paddingHorizontal:30
},
chefHatImg:{
    width:60,
    height:60,
    // marginTop:55
},
cookeasytxt:{
    fontSize:48,
    color:"#483D8B",
    fontWeight:'bold',


},
inputview:{
    paddingVertical:45
},
textinput:{
    width:300,
    backgroundColor:"#D9D9D9",
    marginBottom:24,
    height:60,
    borderRadius:15,
  fontSize:20,
  paddingHorizontal:15,
},
btn:{
    width:260,
    height:61,
    backgroundColor:'#E4BD30',
    borderRadius:20,
    justifyContent:'center',
    alignItems:'center'
},
btntxt:{
    fontSize:25,
    color:'white',
    fontWeight:'bold'
}


})
